body.script:
        // Initialize AOS
        AOS.init({
            duration: 800, // values from 0 to 3000, with step 50ms
            easing: 'ease-in-out-quad', // default easing for AOS animations
            once: false, // whether animation should happen only once - while scrolling down
            mirror: true, // whether elements should animate out while scrolling past them
        });

        // Lucide Icons
        lucide.createIcons();

        // Mobile Menu Toggle
        const mobileMenuButton = document.getElementById('mobile-menu-button');
        const mobileMenu = document.getElementById('mobile-menu');
        mobileMenuButton.addEventListener('click', () => {
            mobileMenu.classList.toggle('hidden');
        });
        
        // Close mobile menu when a link is clicked
        const mobileMenuLinks = mobileMenu.querySelectorAll('a');
        mobileMenuLinks.forEach(link => {
            link.addEventListener('click', () => {
                mobileMenu.classList.add('hidden');
            });
        });

        // Typing effect for hero section
        const typingTextElement = document.getElementById('typing-text');
        const textToType = "Frontend Developer";
        let charIndex = 0;

        function type() {
            if (charIndex < textToType.length) {
                typingTextElement.textContent += textToType.charAt(charIndex);
                charIndex++;
                setTimeout(type, 100); // Adjust typing speed here
            }
        }
        
        // Start typing effect after a short delay when the page loads
        document.addEventListener('DOMContentLoaded', (event) => {
            setTimeout(type, 1200); // Delay start of typing to sync with fade-in
        });

        // Contact Modal Logic
        const contactModal = document.getElementById('contact-modal');
        const contactModalButton = document.getElementById('contact-modal-button');
        const closeModalButton = document.getElementById('close-modal-button');

        contactModalButton.addEventListener('click', () => {
            contactModal.classList.remove('hidden');
            contactModal.classList.add('flex');
        });

        closeModalButton.addEventListener('click', () => {
            contactModal.classList.add('hidden');
            contactModal.classList.remove('flex');
        });

        // Close modal if overlay is clicked
        contactModal.addEventListener('click', (e) => {
            if (e.target === contactModal) {
                contactModal.classList.add('hidden');
                contactModal.classList.remove('flex');
            }
        });
